import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";

import { CardTable, Pagination, InnerTable, ExportCSV } from "../../common";

import { parseAddress } from '../../common/generalUtils'

const formatDate = (stamp) => {
  const dated = new Date(stamp);
  return `${dated.getMonth() + 1}/${dated.getDate()}/${dated.getFullYear()}`;
};

const formatFee = (fee) => {
  return fee < 0 ? `-$${(Math.abs(fee) / 100).toFixed(2)}` : `$${(fee / 100).toFixed(2)}`;
};

const SettlementDetails = ({
  csvArray,
  settlement,
  viewTransaction,
  total,
  sort,
  setSort,
  page,
  setPage
}) => {


  const generateTableColumns = () => {
    return [
      { className: "transaction-id", label: "Transaction ID", sortable: true },
      { className: "update-date", label: "Update Date", sortable: true },
      { className: "customer-name", label: "Customer Name", sortable: true },
      {
        className: "payment-account",
        label: "Payment Account",
        sortable: true
      },
      { className: "amount numeric", label: "Amount", sortable: true }
    ];
  };

  const generateTableRows = (reports) => {
    return reports.map((item, i) => {
      return {
        columns: [{
            className: "transaction-id",
            content: item.transfer_id
          },
          {
            className: "update-date",
            content: formatDate(item.updated_at)
          },
          {
            className: "customer-name",
            content: item.name
          },
          {
            className: "payment-account",
            content: (
              <span className="payment-account-detail">
                <span
                  className={`pay-theory-card-badge pay-theory-card-${
                    item.card_brand
                      ? item.card_brand.toLowerCase().replace(/_/g, "-")
                      : "unknown"
                  }`}
                />
                ending in {item.last_four}
              </span>
            )
          },
          {
            className: "amount numeric",
            content: formatFee(item.amount)
          }
        ],
        key: item.transfer_id,
        view: () => viewTransaction(item),
        item: item
      };
    });
  };

  return (
    <div className="settlement-details">
        <div className="card-head">
          <div className="id-date">
            <h3>Settlement #{settlement.settlement.batch_id}</h3>
            <p>Settled on {formatDate(settlement.settlement.updated_at)}</p>
          </div>
          <div className="totals">
            <h4>Transfers: {settlement.payment_count}</h4>
            <h4>
              Total Amount:{" $"}
              {((settlement.settlement.total_amount-settlement.settlement.total_fee_amount) / 100).toFixed(2)}
            </h4>
          </div>
        </div>

        <InnerTable
          columns={generateTableColumns()}
          rows={generateTableRows(settlement.payments)}
          sort={sort}
          setSort={setSort}
        />
        <div className="card-footer">
        <ExportCSV
            id="download-link"
            items={parseAddress(csvArray)}
            fileName={`PT-Settlement${settlement.settlement.batch_id}-Payments.csv`}
          />
          {total > 1 ? (
            <Pagination page={page} setPage={setPage} total={total} />
          ) : null}
        </div>
      <style jsx="true">{`
        .settlement-details {
          margin: 0px 24px;
        }
        .card-head {
          display: flex;
          margin: 18px;
          justify-content: space-between;
          align-items: center;
          height: 45px;
        }
        .transaction-id {
          width: 120px;
        }
        .transaction-id p {
          white-space: nowrap;
          overflow: hidden;
          text-overflow: ellipsis;
        }
        .update-date {
          width: 105px;
        }
        .status {
          min-width: 110px;
        }
        .amount {
          width: 70px;
        }
        .payment-account {
          width: 150px;
        }
        .customer-name {
          width: 130px;
        }

        .refund {
          width: 50px !important;
          display: flex;
          align-items: center;
          justify-content: center;
        }

        .pay-theory-card-badge {
          background-repeat: no-repeat;
          background-size: 100%;
          background-position: 50%;
          height: 40px;
          width: 45px;
          align-self: center;
          margin-right: 5px;
        }
        .payment-account-detail {
          display: flex;
        }
        .pay-theory-card-visa {
          background-image: url(https://assets.paytheory.com/visa-badge-icon.svg);
        }

        .pay-theory-card-mastercard {
          background-image: url(https://assets.paytheory.com/mastercard-badge-icon.svg);
        }

        .pay-theory-card-american-express {
          background-image: url(https://assets.paytheory.com/amex-badge-icon.svg);
        }

        .pay-theory-card-discover {
          background-image: url(https://assets.paytheory.com/discover-badge-icon.svg);
        }

        .card-head .totals,
        .card-head .id-date {
          display: flex;
          flex-direction: column;
          justify-content: space-between;
          height: 100%;
        }

        .card-head .totals {
          align-items: flex-end;
        }

        .table-wrapper {
          margin: 0px 18px !important;
          border-radius: 5px;
        }

        .card-footer {
          height: 40px;
          margin: 10px 18px;
          width: auto;
          display: flex;
          align-items: center;
          justify-content: center;
          position: relative;
        }

         .export-csv {
            position: absolute;
            left: 24px;
          }

      `}</style>
    </div>
  );
};

SettlementDetails.propTypes = {
  csvArray: PropTypes.array.isRequired,
  settlement: PropTypes.object.isRequired,
  viewTransaction: PropTypes.func.isRequired,
  total: PropTypes.number.isRequired,
  sort: PropTypes.object.isRequired,
  setSort: PropTypes.func.isRequired,
  page: PropTypes.number.isRequired,
  setPage: PropTypes.func.isRequired
};

export default SettlementDetails;
